import React, { Component } from 'react';
import Dice from '../Dice/Dice';
import './styles.css';
import { Title, RollDiceContainer, Button, MainContent } from './styled';

class RollDice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dice: 'one',
      rolling: false,
    };
    this.roll = this.roll.bind(this);
  }
  roll() {
    const { sides } = this.props;
    this.setState({
      dice: sides[Math.floor(Math.random() * sides.length)],
      rolling: true,
    });

    setTimeout(() => {
      this.setState({ rolling: false });
    }, 600);
  }

  render() {
    const { dice, rolling } = this.state;

    return (
      <div className="RollDice">
        <Title>Rolling (6-faced)dice game</Title>
        <RollDiceContainer>
          <Dice face={dice} rolling={rolling} />
        </RollDiceContainer>
        <Button
          className={this.state.rolling ? 'RollDice-rolling' : ''}
          disabled={this.state.rolling}
          onClick={this.roll}
        >
          {this.state.rolling ? 'Rolling' : 'Click to roll'}
        </Button>
      </div>
    );
  }
}

export default RollDice;
