import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './styles.css';
import styled from 'styled-components';

const StoppedDice = styled.FontAwesomeIcon`
  font-size: 10em;
  padding: 0.25em;
  color: #00baff;
`;

const Dice = props => {
  return (
    <div>
      {/* <FontAwesomeIcon
        icon={['fas', `fa-dice-${props.face}`]}
        className={`Dice ${props.rolling && 'Dice-rolling'}`}
      /> */}
      <StoppedDice icon={['fas', `fa-dice-${props.face}`]} />
    </div>
  );
};

export default Dice;
